import React, { Component } from "react";
import Decrement from "../Decrement/index";
class Display extends Component{
    constructor(props) {
        super(props);
        this.displayTasks = this.displayTasks.bind(this);
        this.deleteTask = this.deleteTask.bind(this);
        this.key = this.key.bind(this);
    }
    displayTasks(task) {    
        return <li key={ task.key }>{ task.text }
            {/* <button onClick={() => this.deleteTask(task.key)}>x</button> */}
            <Decrement deleteTask={this.props.deleteTask} key={task.key}/>
            </li>}

    deleteTask(taskkey) {
        this.props.deleteTask(taskkey);
    }

    render() {
        var tasks = this.props.tasks;
        var listTasks = tasks.map(this.displayTasks);
        return (
            <ul className="theList">
                {listTasks}
            </ul>
        )
    }
}
export default Display;
